            <TextField
                id="outlined-multiline-static"
                label="Pytanie"
                placeholder="WprowadÅº pytanie"
                name={props.question.id}
                multiline
                rows="4"
                value={props.question.question}
                onChange={event => props.onChange(props.question.id, event.target.value)}
                className="questionInput"
                margin="normal"
                variant="outlined"
            />

        </div>
    )
}

